class Solution {
  public:
  int dp(vector<vector<int>>& matrix, int n, int m, int i, int j, vector<vector<int>> &t)
    {
        if(j >= m || i >= n || i < 0 || j < 0)
            return 0;
        if(t[i][j] != -1)
            return t[i][j];
        if(i > 0 && matrix[i][j] < matrix[i - 1][j])
        {
            // sum+=matrix[i][j];
            return t[i][j] = max(1 + dp(matrix, n, m, i - 1, j, t), dp(matrix, n, m, i, j, t));
        }
        if(j > 0 && matrix[i][j] < matrix[i][j - 1])
        {
            // sum+=matrix[i][j];
            return t[i][j] = max(1 + dp(matrix, n, m, i, j - 1, t), dp(matrix, n, m, i, j, t));
        }
        if(i < n - 1 && matrix[i + 1][j] > matrix[i][j])
        {
            // sum+=matrix[i][j];
            return t[i][j] = max(1 + dp(matrix, n, m, i + 1, j, t), dp(matrix, n, m, i, j, t));
        }
        if(j < m - 1 && matrix[i][j + 1] > matrix[i][j])
        {
            // sum+=matrix[i][j];
            return t[i][j] = max(1 + dp(matrix, n, m, i, j + 1, t), dp(matrix, n, m, i, j, t));
        }
        return 0;
    }
    int longestIncreasingPath(vector<vector<int>>& matrix) {
        int n = matrix.size();
        int m = matrix[0].size();
        vector<vector<int>> t(n, vector<int>(m, -1));
        int mx = -1;
        dp(matrix, n, m, 0, 0, t);
        for(int i = 0; i < n; i++)
        {
            for(int j = 0; j < m; j++)
                mx = max(t[i][j], mx);
        }
        return mx;
    }
};

