class Solution:
    def levelOrder(self, root: 'Node') -> List[List[int]]:
        ans = []

        if not root:
            return

        def bsf(nodes):
            if not nodes:
                return

            children, nextNodes = [], []
            for node in nodes:
                children.append(node.val)
                nextNodes += node.children
            ans.append(children)
            bsf(nextNodes)

        ans.append([root.val])
        bsf(root.children)
        return ans
