                 store.push_back(q.front()->val);
                 for(auto i : q.front()->children)v.push_back(i);
                 q.pop();  
            }

            for(auto i : v)q.push(i);                // when queue becomes empty , push in all nodes of the next level stored in v
            res.push_back(store);

        }


        return res;
