class Solution:
    def levelOrder(self, root: 'Node') -> List[List[int]]:
        ans = []
        def helper(curr = root, level = 0):
            nonlocal ans
            if curr:
                if len(ans) > level:
                    ans[level].append(curr.val)
                else:
                    ans.append([curr.val])
                for i in curr.children:
                    helper(i, level + 1)
            return
        helper()
        return ans
