/**
 * Definition for a Node.
 * class Node(var _value: Int) {
 *   var value: Int = _value
 *   var children: List[Node] = List()
 * }
 */

object Solution {
    def levelOrder(root: Node): List[List[Int]] = {
	   @annotation.tailrec
        def levelTraversal(listNodes: List[Node], values: List[List[Int]]): List[List[Int]] = {
            if(listNodes.isEmpty) values
            else {
                val levelValue = listNodes.map(node => node.value)
                val nextLevel = listNodes.foldLeft(List.empty[Node]){ (res, node) =>
                    res ++ node.children
                }
                levelTraversal(nextLevel, levelValue :: values)
            }
        }

        val initList = Option(root).fold(List.empty[Node])(_ :: List.empty[Node])
        levelTraversal(initList, List.empty[List[Int]]).reverse    
    }
}
