/*
// Definition for a Node.
class Node {
public:
    int val;
    vector<Node*> children;

    Node() {}

    Node(int _val) {
        val = _val;
    }

    Node(int _val, vector<Node*> _children) {
        val = _val;
        children = _children;
    }
};
*/

class Solution {
public:
    void levelOrderTraversal(Node* root,vector<int> &row,vector<vector<int> > &v){
    queue<Node*> q;
    q.push(root);
    q.push(NULL);
    while(!q.empty()){
        Node* temp= q.front();
        q.pop();
        if(temp==NULL){//previous level has been traversed
        v.push_back(row);
            row.clear();
        if(!q.empty()){//queue still has some child nodes
        q.push(NULL);
        }
        }
        else
        {
            row.push_back(temp->val);
            for(auto child:temp->children){
            if(child){
            q.push(child);
        }
        }
        }
    }
    }
    vector<vector<int>> levelOrder(Node* root) {
        vector<vector<int> > v;
        vector<int> row;
        if(root==NULL)
            return v;
        levelOrderTraversal(root,row,v);
        return v;
    }
};
```

