		Memory: O(n)
		"""

		def levelOrder(self, root: Optional['Node']) -> List[List[int]]:
			levels = defaultdict(list)

			for node, depth in self._walk(root):
				levels[depth].append(node.val)

			return [levels[d] for d in sorted(levels)]

