class Solution {
public:
    vector<vector<int>> levelOrder(Node* root) {
        if(root==NULL)
            return {};
        vector<vector<int>>ans;
        queue<Node*> tree;
        tree.push(root);
        ans.push_back({root->val});
        while(!tree.empty())
        {
            int n=tree.size();
            vector<int>vec;
            while(n--)
            {
            Node* cur=tree.front(); // storing the current node
            tree.pop();
                for(int i=0;i<cur->children.size();i++) // traversing the children of current node
                {
                    tree.push(cur->children[i]); // storing all the children of current node in queue
                    vec.push_back(cur->children[i]->val);
                }
            }
            if(vec.size()>0)
            ans.push_back(vec);
        }
        return ans;
    }
};
