class Solution {
public:
    vector<vector<int>> levelOrder(Node* root) {
        vector<vector<int>> ans;
        if(!root) return ans;

        queue<Node*> q;
        q.push(root);

        while(!q.empty()){
            int len = q.size();
            vector<int>v;
            for(int i = 0 ; i < len ; i++){
                Node* n = q.front();
                v.push_back(n->val);
                q.pop();
                for(auto &it : n->children){
                    q.push(it);
                }
            }
            ans.push_back(v);
        }
        return ans;
    }
};
