func levelOrder(root *Node) [][]int {
    var result [][]int

    if root == nil { return result }

    var queue []*Node
    enqueue(&queue, root)

    for len(queue) > 0 {
        queueSize := len(queue)
        var levelNodes []int

        for i := 0; i < queueSize; i++ {
            dequeuedEle := dequeue(&queue)

            levelNodes = append(levelNodes, dequeuedEle.Val)

            for i := 0; i < len(dequeuedEle.Children); i++ {
                if dequeuedEle.Children[i] != nil {
                    enqueue(&queue, dequeuedEle.Children[i])
                }
            }
        }

        result = append(result, levelNodes)
    }

    return result
}

func enqueue(queue *[]*Node, item *Node) {
    if queue == nil { panic("nil pointer") }

    *queue = append(*queue, item)
} 

func dequeue(queue *[]*Node) *Node {
    if queue == nil { panic("nil pointer") }

    dequeuedElement := (*queue)[0]

    *queue = (*queue)[1:]

    return dequeuedElement
}

