/**
 * // Definition for a Node.
 * function Node(val,children) {
 *    this.val = val;
 *    this.children = children;
 * };
 */

/**
 * @param {Node|null} root
 * @return {number[][]}
 */
var levelOrder = function(root) {
    if (!root) return [];

    let queue = [[root, 0]];
    let currentLevel = 0;
    let out = [[]];

    while (queue.length != 0) {

        let [elem, level] = queue.shift();
        if (level == currentLevel) {
            out[currentLevel].push(elem.val);
        } else {
            currentLevel++;
            out.push([elem.val]);
        }
        elem.left != null ? queue.push([elem.left, level + 1]): null;
        elem.right != null ? queue.push([elem.right, level + 1]): null;


        for (let i = 0; i < elem.children.length; i++) {
            queue.push([elem.children[i], level + 1]);
        }
    }
    return out;
};
```

