class Solution {
public:
    vector<vector<int>> levelOrder(Node * root) {
        vector<vector<int>> levelOrder;
        queue<pair<Node *,int>> q;

        if(root) q.emplace(root, 0);

        while(!q.empty()) {
            auto front = q.front(); q.pop();
            auto node = front.first;
            auto lvl = front.second, value = node->val;
            auto children = node->children;

            if(levelOrder.size() <= lvl) levelOrder.emplace_back();

            levelOrder[lvl].push_back(value);

            for(auto child: children) q.emplace(child, lvl+1);
        }

        return levelOrder;
    }
};
