func levelOrder(root *Node) [][]int {
    if root == nil {
		return [][]int{}
	}

	var q []*Node
	var out [][]int

    q = append(q,root)

    for len(q) != 0 {
		var levelRes []int
		size := len(q)

		for i := 0; i < size; i++ {
			//Equivalent of queue.front()
			top := q[0]
			levelRes = append(levelRes, top.Val)

			for i := 0; i < len(top.Children); i++ {
                q = append(q, top.Children[i])
			}
			//Equivalent of queue.dequeue()
            q = q[1:]
		}
		out = append(out, levelRes)
	}
	return out
}
