                size--;

                vector<Node*> c=cur->children;

                for(int i=0;i<c.size();i++) {
                    q.push(c[i]);
                    a.push_back(c[i]->val);
                }
            }
            if(a.size()!=0) {
                ans.push_back(a);
            }

        }

        return ans;
    }
