        m <<= 1;

        complex *a=new complex[m+1];
        complex *b=new complex[m+1];

        memset(a, 0, (m + 1)*sizeof(complex)); 
        memset(b, 0, (m + 1)*sizeof(complex)); 
        int la = 0; 
        for (int i = l1 - 1; i >= 0; i -= 5) { 
            int tmp = 0; 
            for (int j = i - 4; j <= i; ++j) { 
                if (j < 0) continue; 
                tmp = tmp * 10 + num1[j] - '0'; 
            } 
            a[la++] = complex(tmp, 0); 
        } 

        int lb = 0; 
        for (int i = l2 - 1; i >= 0; i -= 5) { 
            int tmp = 0; 
            for (int j = i - 4; j <= i; ++j) {
                if (j < 0) continue; 
                tmp = tmp * 10 + num2[j] - '0'; 
            } 
            b[lb++] = complex(tmp, 0);
        } 

        int l = max(la, lb);
        int n = 1; 
        while (n < l) n <<= 1; 
        n <<= 1;

        long long *ans=new long long[n+10];

        fft(a, n, 0); 
        fft(b, n, 0); 
        for(int i = 0; i < n; i++) a[i] = a[i] * b[i]; 
        fft(a, n, 1); 

