class Solution {
public:
    void makeLengthEqual(string & a,string &b){
        if(a.size()==b.size())
            return;
        if(a.size()<b.size()){
            makeLengthEqual(b,a);
            return;
        }
        while(b.size()!=a.size()){
            b="0"+b;
        }
    }
    void removeLeadingZero(string &num){
        int n=num.size()-1;
        for(int i=0;i<num.size()-1;i++){
            if(num[i]!='0'){
                n=i;
                break;
            }
        }
        num=num.substr(n);
    }
    string multiply(string num1, string num2) {
        makeLengthEqual(num1,num2);
        int n=num1.size();
        int carry=0;
        string ans="";
        for(int i=n-1;i>=0;i--){
            int sum=0;
            int k=i;
            for(int j=n-1;j>=i;j--){
                sum+=(num1[j]-'0')*(num2[k]-'0');
                k++;
            }
            sum+=carry;
            carry=sum/10;
            ans+=('0'+(sum%10));
        }
        for(int i=n-2;i>=0;i--){
            int sum=0;
            int k=0;
            for(int j=i;j>=0;j--){
                sum+=(num1[j]-'0')*(num2[k]-'0');
                k++;
            }
            sum+=carry;
            carry=sum/10;
            ans+=('0'+(sum%10));
        }
        if(carry)
            ans+=('0'+carry);
        reverse(ans.begin(),ans.end());
        removeLeadingZero(ans);
        return ans;
    }
};
