        int i_multipliar, i_carry = 0, i_multiplicand = 0;

        for (i = num2.length() - 1; i >= 0; i--)
        {
            i_multipliar = num2[i] - '0';
            for (j = num1.length() - 1; j >= 0; j--)
            {
                i_multiplicand = num1[j] - '0';
				//compute new product from multipliar, multiplicand and previous partial product
                i_carry = i_multiplicand * i_multipliar + i_carry + result[j + i + 1]; 
                result[j + i + 1] = i_carry % 10;
                i_carry = i_carry / 10;
