                res[pos2] += prod / 10;
            }
        }
        StringBuilder sb = new StringBuilder();
        for (int i = size1 + size2 - 1; i >= 0; --i){
            if (i == 0 && res[i] == 0)
                continue;
            sb.append(res[i] % 10);
            if (res[i] >= 10){
                res[i-1] += res[i] / 10;
            }
        }
