            double ang = 2 * PI * i / n;
            omega[i] = Complex(cos(ang), sin(ang));
            invert[i] = omega[i].conjugate();

            if (i > 0) {
                rev[i] = rev[i >> 1] >> 1;
                if (i & 1) {
                    rev[i] |= n >> 1;
                }
            }
        }
