            a[i] = num1[len1 - 1 - i] - '0';
        }

        for (int i = 0; i < len2; ++i) {
            b[i] = num2[len2 - 1 - i] - '0';
        }

        ntt(a, false);
        ntt(b, false);

        for (int i = 0; i < n; i++) {
            a[i] = (a[i] * b[i]) % MOD;
        }

        ntt(a, true);

        string res;
        long long carry = 0;
        long long inver = quickPower(n, MOD - 2);

        for (int i = 0; i < n; i++) {
            a[i] = a[i] * inver % MOD;
        }

        for (int i = 0; i < n; i++) {
            long long sum = a[i] + carry;
            res += sum % 10 + '0';
            carry = sum / 10;
        }

        while (carry) {
            res += carry % 10 + '0';
            carry /= 10;
        }

        int idx = n - 1;
        while (idx >= 0 && res[idx] == '0') {
            idx--;
        }

        res = res.substr(0, idx + 1);
        reverse(res.begin(), res.end());
        return res;
    }
}
