class Solution {
public:
    const int base = 1e9;
    vector<int> convert(string& num){
        int len = num.size();
        int sz = (len - 1) / 9 + 1; // Calculate the size of the ans vector
        vector<int> ans(sz, 0);
        int idx = 0, tenPow = 1;
        for (int i = len-1; i>= 0; i--){
            ans[idx] += tenPow*(num[i]-'0');
            tenPow *= 10;
            if (tenPow == base){
                idx++;
                tenPow = 1;
            }
        }
        return ans;
    }

    vector<int> mult(vector<int>& x, vector<int>& y){
        int x_len = x.size(), y_len = y.size();
        int len = x_len + y_len;
        vector<int> z(len);
        long long carry = 0; // Change to long long
        for (int i = 0; i < len; i++){
            long long sum = carry;
            int j0 = min(y_len, i + 1);
            for (int j = max(0, i-x_len+1); j < j0; j++){
                sum += (long long)x[i-j] * y[j];
            }
            auto d=lldiv(sum , base);
            carry = d.quot;
            z[i] = d.rem;
        }
        if (z.size() > 1 && z.back() == 0) z.pop_back(); // Remove trailing zeros.
        return z;
    }

    string to_str(vector<int> z){
        int sz = z.size();
        string ans = to_string(z[sz - 1]);
        for (int i = sz - 2; i >= 0; i--){
            string tmp = to_string(z[i]);
            int l = tmp.size();
            tmp = string(9 - l, '0') + tmp;
            ans += tmp;
        }
        return ans;
    }

    string multiply(string num1, string num2) {
        vector<int> x = convert(num1);
        vector<int> y = convert(num2);
        vector<int> z = mult(x, y);
        return to_str(z);
    }
};

