class Solution {
public:
    const int base = 1e9; // Base to represent chunks of 9 digits

    vector<int> convert(string& num){
        int len = num.size();
        int sz = (len - 1) / 9 + 1; // Calculate the size of the ans vector to store chunks of 9 digits
        vector<int> ans(sz, 0); // Initialize the ans vector with zeros
        int idx = 0, tenPow = 1; // Initialize index and a variable for powers of 10
        for (int i = len - 1; i >= 0; i--){
            ans[idx] += tenPow * (num[i] - '0'); // Convert the character to integer and add it to the appropriate chunk
            tenPow *= 10; // Increase the power of 10 for the next iteration
            if (tenPow == base){ // If the current chunk is full (i.e., reached base)
                idx++; // Move to the next chunk
                tenPow = 1; // Reset the power of 10
            }
        }
        return ans;
    }

    vector<int> mult(vector<int>& x, vector<int>& y){
        int x_len = x.size(), y_len = y.size();
        int len = x_len + y_len; // Calculate the length of the resulting vector after multiplication
        vector<int> z(len); // Initialize the resulting vector with zeros
        long long carry = 0; // Use long long to handle large intermediate results
        for (int i = 0; i < len; i++){
            long long sum = carry; // Initialize the sum with the carry from the previous iteration
            int j0 = min(y_len, i + 1); // Define the bound of the inner loop
            for (int j = max(0, i - x_len + 1); j < j0; j++){ // Iterate through appropriate elements of x and y vectors
                sum += (long long)x[i - j] * y[j]; // Multiply corresponding elements and add to the sum
            }
            auto d = lldiv(sum, base); // Perform a long division of sum by base to get quotient and remainder
            carry = d.quot; // Set the carry for the next iteration
            z[i] = d.rem; // Store the remainder in the resulting vector
        }
        if (z.size() > 1 && z.back() == 0) z.pop_back(); // Remove trailing zeros from the resulting vector
        return z;
    }

    string to_str(vector<int> z){
        int sz = z.size();
        string ans = to_string(z[sz - 1]); // Initialize the answer with the most significant chunk
        for (int i = sz - 2; i >= 0; i--){
            string tmp = to_string(z[i]); // Convert the current chunk to string
            int l = tmp.size();
            tmp = string(9 - l, '0') + tmp; // Add leading zeros to non-most significant chunks
            ans += tmp; // Concatenate the current chunk to the answer
        }
        return ans;
    }

    string multiply(string num1, string num2) {
        vector<int> x = convert(num1); // Convert the first number to vector representation
        vector<int> y = convert(num2); // Convert the second number to vector representation
        vector<int> z = mult(x, y); // Multiply the two vectors
        return to_str(z); // Convert the resulting vector back to string representation
    }
};

