        string ans = to_string(z[sz - 1]); // Initialize the answer with the most significant chunk
        for (int i = sz - 2; i >= 0; i--){
            string tmp = to_string(z[i]); // Convert the current chunk to string
            int l = tmp.size();
            tmp = string(9 - l, '0') + tmp; // Add leading zeros to non-most significant chunks
            ans += tmp; // Concatenate the current chunk to the answer
        }
        return ans;
    }

    string multiply(string num1, string num2) {
        vector<int> x = convert(num1); // Convert the first number to vector representation
        vector<int> y = convert(num2); // Convert the second number to vector representation
        vector<int> z = mult(x, y); // Multiply the two vectors
        return to_str(z); // Convert the resulting vector back to string representation
    }
};

