class Solution {
    func multiply(_ num1: String, _ num2: String) -> String {

        //Figure out smaller and larger string...
        var smallString = num1, largeString = num2
        if smallString.count > largeString.count { smallString = num2; largeString = num1 }

        //We work with ints, not with characters.
        var small = smallString.compactMap { Int(String($0)) }
        var large = largeString.compactMap { Int(String($0)) }

        if small.count <= 0 { return "0" }
        if large.count <= 0 { return "0" }

        //Build the matrix which will store our "rows" a la "by hand" multiplication...
        var length = small.count + large.count + 1
        var workout = [[Int]](repeating: [Int](repeating: 0, count: length), count: small.count)

        var i = small.count - 1
        var shift = 0

        //Multiply number by number and store in matrix.
        while i >= 0 {
            var val1 = small[i]
            var carry = 0
            var j = large.count - 1
            var paste = length - (1 + shift)
            while j >= 0 {
                var val2 = large[j]
                var num = val1 * val2 + carry
                workout[i][paste] = num % 10
                carry = num / 10
                j -= 1
                paste -= 1
            }
            while carry > 0 {
                workout[i][paste] = carry % 10
                carry /= 10
                paste -= 1   
            }
            shift += 1
            i -= 1
        }

        var j = length - 1


        var res = [Int](repeating: 0, count: length)

        //Add the lines of our workout together.
        var carry: Int = 0
        while j >= 0 {
            var sum: Int = carry
            i = 0
            while i < small.count {
                sum += workout[i][j]
                i += 1
            }
            res[j] = sum % 10
            carry = sum / 10
            j -= 1
        }

        //Skip the dangling zeroes...
        var zeroIndex = 0
        while zeroIndex < length && res[zeroIndex] == 0 { zeroIndex += 1 }
        if zeroIndex == length { zeroIndex = length - 1 }
        res = Array(res[zeroIndex...])

        //Use swift magic to quickly derive a string from this array..!
        return res.map { String($0) }.joined()
    }
}
