class Solution {
    public int compress(char[] chars) {
        int i=0;
        int n=chars.length;
        int j=0;
        if(n==1)
            return 1;
        while(i<n){
            int count=1;
            char charac=chars[i];
            while(i+1<n && chars[i]==chars[i+1]){
                count++;
                i++;
            }
            if(count==1){
                chars[j++]=charac;
