            elif count>1:
                cc=[*str(count)]
                for j in range(len(cc)):
                    chars.insert(i+j+1,cc[j])
                count=1
                i+=len(cc)+1
            else:
                i+=1
        if count>1:
            chars+=[*str(count)]
