public class Solution {
    public int Compress(char[] chars) {
        // Keeps track of the result index
        var resultIndex = 0;

        // Go through items in the array
        var currentIndex = 0;
        while (currentIndex < chars.Length) {
            // Get the current character
            var currentLetter = chars[currentIndex];

            // Check to see how many consecutive characters exists
            var letterCount = 0;
            while (currentIndex < chars.Length && chars[currentIndex] == currentLetter) {
                ++currentIndex;
                ++letterCount;
            }
            // Save the current character at the result index
            chars[resultIndex++] = currentLetter;

            // Save the consecutive character count if there is more than one letter
            if (letterCount > 1) {
                foreach (var digit in letterCount.ToString()) {
                    chars[resultIndex++] = digit;
                }
            }
        }        
        return resultIndex;
    }
}
```

