class Solution {
    public int compress(char[] chars) {
        // we only need to count the current char's count and see what it is
        int result = 0;
        if (chars == null || chars.length == 0){
            return 0;
        }
        if (chars.length == 1){
            return 1;
        }
        // need to change the original array
        // [0,i) : what we are going to return 
        // j: current, k - j = count of current char
        // k: fast pointer to find the next diff char
        int i = 0;
        int j = 0;
        int k = 0;
        while (j < chars.length){
            // find next char
            while (k < chars.length && chars[k] == chars[j]){
                k++;
            }
            int count = k - j;

            // udpate arr using i
            chars[i] = chars[j];
            int len = 0;
            if (count != 1){
                while (count != 0){
                    count /= 10;
                    len++;
                }
                count = k - j;
                i += len;
                while (count != 0){
                    chars[i--] = (char)(count % 10 + '0');
                    count /= 10;
                }
            }

            // update i,  k and j
            i += len + 1;
            j = k;
        }
        return i;
    }
}
