class Solution {
  int compress(List<String> chars) {
      var prev = chars[0];
      var counter = 1;
      var index = 1;
      while (index < chars.length) {
          var char = chars[index];
          if (char != prev) {
              if (counter > 1) {
                  chars.insertAll(index, counter.toString().split(""));
                  index += counter.toString().length;
              }
              index++;
              prev = char;
              counter = 1;
          }
          else {
              counter += 1;
              chars.removeAt(index);
          }
      }

      if (counter > 1) {
          chars.insertAll(index, counter.toString().split(""));
      }
      return chars.length;
  }
}
