        n = len(chars)
        if n == 1:
            return 1

        i, j = 0, 0
        while i < n:
            count = 1
            while i < n - 1 and chars[i] == chars[i + 1]:
                count += 1
                i += 1

            chars[j] = chars[i]
            j += 1
            i += 1

            if count > 1:
                countStr = str(count)
                for k in range(len(countStr)):
                    chars[j] = countStr[k]
                    j += 1

        return j

