class Solution {
public:
    int compress(vector<char>& chars) {
        int n = chars.size();
        int idx = 0;
        for(int i = 0; i<n;i++){
            int cnt = 1;
            char c =chars[i];
            while(i+1<n&&chars[i]==chars[i+1]){
                c = chars[i]; cnt++; i++;
            }
            if(cnt==1){
                chars[idx++] = c;
            }
            else{
                if(cnt>1){
                    chars[idx++] = c;
                }
                string s = to_string(cnt);
                for(auto it : s){
                    chars[idx++] = it;
                }
            }
        }
        return idx;
    }
};

