    int jump(vector<int>& nums) {
        int n = nums.size();
        return f(0, n, nums);
    }

	int f(int ind, int n, vector<int>& nums) {
        if(ind == n-1) return 0;

        int jump = nums[ind], mini = 1e9;
        for(int i = ind + 1; i <= min(ind+jump, n-1); i++) {
            int val = 1 + f(i, n, nums);
            mini = min(mini, val);
        }
        return mini;
