long long decideJump(vector<int>& nums,int n,int currPos){
    if(currPos >= n-1){
        return 0;
    }

    long long answer = INT_MAX;

    int maxSteps = nums[currPos];


    while(maxSteps > 0){
        answer = min(answer,1 + decideJump(nums,n,currPos+maxSteps));
        maxSteps--;
    }

    return answer;
}


class Solution {
