    public int maxResult(int[] nums, int k) {
        Deque<Integer> dq = new ArrayDeque<>();
        dq.offer(0);

        for(int i = 1; i < nums.length; i++) {
            nums[i] += nums[dq.peekFirst()];

            while(!dq.isEmpty() && nums[dq.peekLast()] <= nums[i])
                dq.pollLast();
            dq.offer(i);

            if(i - dq.peekFirst() >= k) dq.pollFirst();
