class Solution {
public:
    int n;
    int solve(int i, vector<int> &nums)
    {
        if(i == n-1)  //if reached last index then no need to move further so no steps required
            return 0;

        int mini = INT_MAX-1;                       //-1 bc in loop 1 + solve(), if solve() returns INT_MAX, 1+INT_MAX will result stack overflow
        for(int j=1; j<=min(n-1 - i, nums[i]); j++) //possible steps we can take from i
        {
            int temp = 1 + solve(i+j, nums); //at i take 1 step of length j, so we reach i+j
            mini = min(mini, temp);          //we take minimum of all steps from i to reach n-1
        }

        return mini;  //if not reached n-1, then it will not go in loop and return mini = INT_MAX-1 at some step where further going forward is not possible, back trackingto previous call temp = 1 + INT_MAX-1, mini = min(mini, INT_MAX), so we see for current j it will get INT_MAX as temp ans, while taking minimum it won't affect the answer
    }

    int jump(vector<int>& nums) 
    {
        n = nums.size();

        return solve(0, nums, dp);
    }
};
