    }

    record := make(map[int]int)
    // key: index of num
    // value: min amount of steps to reach the end

    lastIdx := len(nums) - 1

    for i := lastIdx - 1; i > -1; i-- {
        if nums[i] >= lastIdx - i {
            record[i] = 1
        } else {
            for key, value := range record {
                if nums[i] >= key - i {
                    if _, exist := record[i]; exist {
                        record[i] = min(value + 1, record[i])
                    } else {
                        record[i] = value + 1
                    }
                }
            }
        }
    }

    return record[0]
}

