here js = jumps, m = next index where we jump
int jump(int* nums, int n){
    int js = 0, m;
    if (n == 1) return 0;
    for (int i = 0; i < n; ){
        if (i + nums[i] >= n-1) return ++js;
        m = i+1;
        for (int j = i+2, k = nums[i]-1; k > 0; j++, k--)
            if ((nums[j] + j) >= (nums[m] + m)) m = j;
         i = m;
         ++js;
    }
    return js;
}

