```
    // we will maintain two stacks named as "parent" and "preCompute";
    // logic is to directly build postorder taversal with the help of parent stack
    vector<int> postorderTraversal(TreeNode* root) {

        if(!root)   return {};
        stack<TreeNode*> parent , preCompute;
        parent.emplace(root);

        while(parent.size())
        {
            TreeNode *temp = parent.top();  
            parent.pop();
            if(temp->left)
                parent.emplace(temp->left);
            if(temp->right)
                parent.emplace(temp->right);
            preCompute.emplace(temp);                
        }
        vector<int> ans;
        while(preCompute.size())
            ans.emplace_back( preCompute.top()->val) , preCompute.pop();
        return ans;
    }
};
```
