```
    public List<Integer> postorderTraversal(TreeNode root) {
        List<Integer> res = new ArrayList<Integer>();
        Stack<TreeNode> stack = new Stack<TreeNode>();

        if(root == null) return res;
        stack.push(root);

        while(!stack.isEmpty()){
            TreeNode node = stack.peek(); // to get the reference of the node on top of stack
            if(node.left == null && node.right == null){ // if it is a leaf, add its value to result
                res.add(node.val);
                stack.pop();
            }else{
                // if it has children, do not remove it from stack
                // now we've got its reference, then it's easy to visit its children
                if(node.right != null) stack.push(node.right);
                if(node.left != null) stack.push(node.left);
                // after adding its children to stack, cut off its leaves.
                // otherwise, the non-leaf nodes will never enter to the if branch
                node.right = null; 
                node.left = null;
            }
        }

        return res;
    }
