class Solution {
public:
    vector<int> postorderTraversal(TreeNode* root) {
        vector<int> aResult;
        stack<TreeNode*> aStack;
        TreeNode *aNode = root;
        while (aStack.size() > 0 || aNode != NULL) {
            if (aNode == NULL) {
                aNode = aStack.top()->left;
                aStack.pop();
            } else {
                aStack.push(aNode);
                aResult.push_back(aNode->val);
                aNode = aNode->right;
            }
        }
        reverse(aResult.begin(), aResult.end());
        return aResult;
    }
};
