class Solution {
    public String reverseVowels(String s) {
        int start = 0;
        int end = s.length() - 1;
        char[] vowel = new char[] {'a','e','i','o','u'};
        while(start <= end) {
            if(contains(vowel, s.charAt(start))) {
                if(contains(vowel, s.charAt(end))) {
                    s = swap(s,start,end);
                    start++;
                    end--;
                }
                else {
                    end--;
                }
            }
            else {
                start++;
            }
        }
        return s;
    }

    private boolean contains(char[] v, char c) {
        for(char i : v) {
            if(Character.toLowerCase(i) == Character.toLowerCase(c))
                return true;
        }

        return false;
    }

    private String swap(String s, int start, int end) {
        StringBuilder string = new StringBuilder(s);
        char temp = s.charAt(start);
        string.setCharAt(start, s.charAt(end));
        //s[start] = s.charAt(end);
        //s[end] = temp;
        string.setCharAt(end, temp);

        return string.toString();
    }
}
