class Solution {
    public String reverseVowels(String s) {
        Set<Character> v = new HashSet<>(Arrays.asList('a','e','i','o','u','A','E','I','O','U'));

        char[] ch = s.toCharArray();

        int left = 0 , right = s.length()-1;
        while(left < right){

            if(v.contains(ch[left]) && v.contains(ch[right])){
                char c = ch[right];
                ch[right] = ch[left];
                ch[left] = c;
                left++;
                right--;
            }
            else{
                    if(!v.contains(ch[left])){
                        left++;
                }
                    if(!v.contains(ch[right])){
                        right--;
                }
            } 
        }
        return new String(ch);
    }
}
