# The idea is to map the next node to its parent node so that we can move back using map and move forward by next method.
```
/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
        if(!l1)
            return l2;
        if(!l2)
            return l1;

        unordered_map<ListNode*,ListNode*> mp1,mp2;

        ListNode *list1=l1,*list2=l2;

        while(list1) // mapping next node to its parent node
        {
            mp1[list1->next]=list1;
            list1=list1->next;
        }

        while(list2)
        {
            mp2[list2->next]=list2;
            list2=list2->next;
        }

        ListNode *prev=nullptr,*curr=nullptr;  //to desig our desired output link list
        int carry=0;

        while(list1!=l1 && list2!=l2) //now current both list1 and list2 is null. If any will hit the head of any
        {                                          //given input list we will stop
            list1=mp1[list1];
            list2=mp2[list2];

            int sum=list1->val+list2->val+carry;
            carry=sum/10;
            curr=new ListNode(sum%10,prev);
            prev=curr;
        }

        if(list1==l1)
        {
            while(list2!=l2) //when l1 length is greater than l2 length
            {
                list2=mp2[list2];

                int sum=list2->val+carry;
                carry=sum/10;
                curr=new ListNode(sum%10,prev);
                prev=curr;
            }
        }
        else
        {
            while(list1!=l1) //when l2 length is greater than l1 length
            {
                list1=mp1[list1];

                int sum=list1->val+carry;
                carry=sum/10;
                curr=new ListNode(sum%10,prev);
                prev=curr; 
            }
        }

        if(carry>0) //if some value is left in a carry
        {
            curr=new ListNode(carry,prev);
            prev=curr;
        }

        return prev;
    }
};
