first find which linked list is longer
then find the start node to add both lists, we only add numbers and do not care about carry over
then recursively fix the carry over

```
class Solution {
public:
    int check( ListNode * node)
    {
        int carry = 0;
        if(node -> next) carry += check(node -> next);
        int val = carry + node -> val;;
        carry = val / 10;
        node -> val = val % 10;
        return carry;
    }

    ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
        if(!l1) return l2;
        if(!l2) return l1;
        auto node = l1;
        int len1 = 1, len2 = 1;
        while(node -> next)
        {
            node = node -> next;
            len1++;
        }
        node = l2;
        while(node -> next)
        {
            node = node -> next;
            len2++;
        }

        auto n1 = len1 > len2 ? l1 : l2;
        auto n2 = len1 > len2 ? l2 : l1;

        node = n1;
        for(int i = 0; i < abs(len1-len2); i++) node = node -> next;
        while(node)
        {
            node -> val += n2 -> val;
            n2 = n2 -> next;
            node = node -> next;
        }

        if(check(n1) == 0) return n1;
        node = new ListNode(1);
        node -> next = n1;
        return node;
    }
};

