/**
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 * };
 */
class Solution {
public:
//Recursive utility function to reverse linked list
    ListNode* reverse(ListNode* head)
    {
        if(head==NULL || head->next==NULL)
        {
            return head;
        }
        ListNode* rest = reverse(head->next);
        head->next->next=head;
        head->next=NULL;
        return rest;
    }

    ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
	  // if any 1 list is empty, return the other
        if(l1==NULL) return l2;
        if(l2==NULL) return l1;

		//reverse both the lists to start addition from units digit (rightmost digit)
        l1 = reverse(l1);
        l2 = reverse(l2);

		//Initialize a new list with a sentinel node (-1)
        ListNode* newlist=  new ListNode(-1);
        ListNode* temp = newlist;
        int sum=0;
        int carry=0;

		// Loop until both the lists are not empty
        while(l1!=NULL || l2!=NULL)
        {
			// if lists are not null, add the digits to sum variable and add previous carry
            sum = carry;
            if(l1!=NULL)
            {
                sum+=l1->val;
                l1=l1->next;
            }
            if(l2!=NULL)
            {
                sum+=l2->val;
                l2=l2->next;
            }

			// find carry for next iteration and insert new node with current digit as value
            carry = sum/10;
            temp->next = new ListNode(sum%10);
            temp=temp->next;
        }

		// if carry is not 0, add a new node with the value of carry
        if(carry!=0)
        {
            temp->next = new ListNode(carry);
            temp = temp->next;
        }

		// return the reverse of the new list
        return reverse(newlist->next);
    }
};
