# recursive (faster & less memory)
```
class Solution {
public:
    int carry(ListNode* l1, ListNode* l2, int size1, int size2){
        if(!size1)
            return 0;
        int val = l1->val;
        if(size1 > size2)
            val += carry(l1->next, l2, size1 - 1, size2);
        else
            val += l2->val + carry(l1->next, l2->next, size1 - 1, size2 - 1);   
        l1->val = val % 10;
        return val / 10;
    }
    ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
        int size1 = 0, size2 = 0;
        for(ListNode *tmp = l1; tmp; tmp = tmp->next, size1++);
        for(ListNode *tmp = l2; tmp; tmp = tmp->next, size2++);
        if(size2 > size1)
            return addTwoNumbers(l2, l1);
        int c = carry(l1, l2, size1, size2);
        if(c){
            ListNode* tmp = new ListNode;
            tmp->val = c;
            tmp->next = l1;
            l1 = tmp;
        }

        return l1;
    }
};
