    ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
        vector<ListNode **> indirect1, indirect2;
        for(ListNode **indirect = &l1; *indirect; indirect = &(*indirect)->next)
            indirect1.emplace_back(indirect);
        for(ListNode **indirect = &l2; *indirect; indirect = &(*indirect)->next)
            indirect2.emplace_back(indirect);
        int idx1 = indirect1.size() - 1, idx2 = indirect2.size() - 1;
        if(idx2 > idx1)
            return addTwoNumbers(l2, l1);
        int carry = 0;

        for(; idx2 >= 0 || carry; idx1--, idx2--){
            cout << idx2 << '\n';
            int val = carry; 
            val += idx1 >= 0 ? (*indirect1[idx1])->val : 0; 
            val += idx2 >= 0 ? (*indirect2[idx2])->val : 0;
            carry = val / 10;
            val %= 10;
            if(idx1 < 0){
                auto tmp = new ListNode;
                tmp->next = l1;
                l1 = tmp;
                idx1 = 0;
            }
            (*indirect1[idx1])->val = val;
        }

        return l1;
    }
};
