        if( !tmp1 && !tmp2 ) return NULL;

        stack<int> st1, st2;
		// Push val into two stack
        while( tmp1 && tmp2 ){
            st1.push(tmp1->val);
            st2.push(tmp2->val);
            tmp1 = tmp1->next;
            tmp2 = tmp2->next;
        }
        while( tmp1 ){
            st1.push(tmp1->val);
            tmp1 = tmp1->next;
        } 
        while( tmp2 ){
            st2.push(tmp2->val);
            tmp2 = tmp2->next; 
        }

        ListNode* res = NULL;
        int mem = 0; // memo = 0 or memo = 1

        while( !st1.empty() || !st2.empty() ){
            int a = st1.empty() ? 0 : st1.top();
            int b = st2.empty() ? 0 : st2.top();
            res = new ListNode((a + b + mem) % 10, res);// Insert to head of list
            mem = a + b + mem >= 10 ? 1 : 0;// Update memo
            if( !st1.empty() ) st1.pop();
            if( !st2.empty() ) st2.pop();
        }
        if( mem ) res = new ListNode(1, res);// Case memo = 1

        return res;
    }
};
