class Solution {
    public ListNode addTwoNumbers(ListNode l1, ListNode l2) {
        ListNode longlist = l1;
        ListNode shortlist = l2;
        int size1 = 0, size2 = 0;
        ListNode cur = l1;

        // get length of l1 and l2
        while (cur != null) {
            cur = cur.next;
            size1++;
        }
        cur = l2;
        while (cur != null) {
            cur = cur.next;
            size2++;
        }

        // make sure long list is longer than short list
        if (size1 < size2){
            longlist = l2;
            shortlist = l1;
        }
        int dif = Math.abs(size1 - size2); // length difference between l1 and l2

        // if first bit get bigger than 9 
        if (rec(longlist, shortlist, dif) > 0) {
            ListNode n = new ListNode(1);
            n.next = longlist;
            return n;
        }
        return longlist;
    }
    public int rec(ListNode longlist, ListNode shortlist, int dif) {
        if (longlist == null) return 0;
        int sum, c = 0;
        if (dif > 0) {
            c = rec(longlist.next, shortlist, dif - 1);
            sum = c + longlist.val;

        } else {
            c = rec(longlist.next, shortlist.next, dif - 1);
            sum = c + longlist.val + shortlist.val;
        }

        // change number in long node
        longlist.val = sum %10;

        //return add number in previous node
        return sum/10;  
    }   
}
