 * };
 */
class Solution {
public:
    ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
        unordered_map<ListNode*,ListNode*> mp;
        ListNode* templ1 = l1;
        ListNode* templ2 = l2;

